mapper.select:
SELECT EPSV.POS_ID , EPSV.POS_NAME , EPSV.PARENT_POS_ID , EPSV.PARENT_POS_NAME , EPSV.POS_CATE_ID , EPSV.CNAME, EPSV.POS_SUBCATE_ID , EPSV.SNAME , EPSV.LEVEL_CODE , EPSV.ORG_LEVEL , EPSV.CORE_FLAG , EPSV.DEGREE_CODE , EPSV.EDUCATION_DEGREE , EPSV.KSTR , date_format(EPSV.ACTIVE_START_DATE,'yyyy-MM-dd') as ACTIVE_START_DATE , date_format(EPSV.ACTIVE_END_DATE,'yyyy-MM-dd')as ACTIVE_END_DATE FROM EHRCUC_POS_SPOS_V EPSV where 1=1
and epsv.pos_cate_id = #{posCateId}
and epsv.pos_subcate_id = #{posSubcateId}
and epsv.pos_name like concat("%", trim(#{posName}), "%")
and (epsv.level_code like concat('%', #{orgLevelS}, '%') or epsv.level_code like concat('%', #{orgLevelD}, '%') or epsv.level_code like concat('%', #{orgLevelX}, '%'))
and epsv.degree_code = #{educationDegree}
and epsv.core_flag = #{coreFlag}
and SYSDATE BETWEEN IFNULL(EPSV.ACTIVE_START_DATE,SYSDATE - 1) AND IFNULL (EPSV.ACTIVE_END_DATE,SYSDATE + 1)
and SYSDATE NOT BETWEEN IFNULL(EPSV.ACTIVE_START_DATE,SYSDATE - 1) AND IFNULL(EPSV.ACTIVE_END_DATE,SYSDATE + 1)
and 1 != 1
and epsv.org_id IN (SELECT RNG.ORGANIZATION_ID FROM HADES_RANGE_ORGANIZATION RNG WHERE RNG.RANGE_ID = #{rangeId})

SELECT EPSV.POS_ID , EPSV.POS_NAME , EPSV.PARENT_POS_ID , EPSV.PARENT_POS_NAME , EPSV.POS_CATE_ID , EPSV.CNAME , EPSV.POS_SUBCATE_ID , EPSV.SNAME , EPSV.LEVEL_CODE , EPSV.ORG_LEVEL , EPSV.CORE_FLAG , EPSV.DEGREE_CODE , EPSV.EDUCATION_DEGREE , EPSV.KSTR , date_format(EPSV.ACTIVE_START_DATE,'yyyy-MM-dd') as ACTIVE_START_DATE, date_format(EPSV.ACTIVE_END_DATE,'yyyy-MM-dd') as ACTIVE_END_DATE FROM EHRCUC_POS_SPOS_V EPSV where 1=1
and epsv.pos_cate_id = #{posCateId}
and epsv.pos_subcate_id = #{posSubcateId}
and epsv.pos_name like concat("%", trim(#{posName}), "%")
and (epsv.level_code like concat('%', #{orgLevelS}, '%') or epsv.level_code like concat('%', #{orgLevelD}, '%') or epsv.level_code like concat('%', #{orgLevelX}, '%'))
and epsv.degree_code = #{educationDegree}
and epsv.core_flag = #{coreFlag}
and now() BETWEEN IFNULL(EPSV.ACTIVE_START_DATE,date_sub( now( ) , INTERVAL 1 DAY )) AND IFNULL (EPSV.ACTIVE_END_DATE,DATE_ADD( now( ), INTERVAL 1 DAY ))
and now() NOT BETWEEN IFNULL(EPSV.ACTIVE_START_DATE,date_sub( now( ) , INTERVAL 1 DAY )) AND IFNULL(EPSV.ACTIVE_END_DATE,DATE_ADD( now( ), INTERVAL 1 DAY ))
and 1 != 1
and epsv.org_id IN (SELECT RNG.ORGANIZATION_ID FROM HADES_RANGE_ORGANIZATION RNG WHERE RNG.RANGE_ID = #{rangeId})

SELECT EPJV.POS_ID , EPJV.POS_NAME , EPJV.POS_CATE_ID , EPJV.CNAME , EPJV.POS_SUBCATE_ID , EPJV.SNAME , EPJV.LEVEL_CODE , EPJV.ORG_LEVEL , EPJV.CORE_FLAG , EPJV.DEGREE_CODE , EPJV.EDUCATION_DEGREE , EPJV.KSTR , case when ifnull(C.SNUM, 0)=0 then '否' else '是' end, date_format(EPJV.ACTIVE_START_DATE,'%Y-%m-%d %H:%i:%s') as ACTIVE_START_DATE, date_format(EPJV.ACTIVE_END_DATE,'%Y-%m-%d %H:%i:%s')as ACTIVE_END_DATE
FROM EHRCUC_POS_JPOS_V EPJV left join (SELECT EPS.PARENT_POS_ID, COUNT(1) SNUM FROM EHRCUC_POS_STANDARDPOS EPS WHERE 1 = 1 AND EPS.POS_TYPE = 'S' GROUP BY EPS.PARENT_POS_ID) C on EPJV.POS_ID = C.PARENT_POS_ID where 1=1
and epjv.pos_cate_id = #{posCateId}
and epjv.pos_subcate_id = #{posSubcateId}
and epjv.pos_name like concat("%", trim(#{posName}), "%")
and epjv.mflag = #{mFlag}
and epjv.degree_code = #{educationDegree}
and epjv.core_flag = #{coreFlag}
and now() BETWEEN IFNULL(EPJV.ACTIVE_START_DATE,date_sub( now( ) , INTERVAL 1 DAY )) AND IFNULL (EPJV.ACTIVE_END_DATE, DATE_ADD( now( ), INTERVAL 1 DAY ))
and now() NOT BETWEEN IFNULL(EPJV.ACTIVE_START_DATE,date_sub( now( ) , INTERVAL 1 DAY )) AND IFNULL(EPJV.ACTIVE_END_DATE,DATE_ADD( now( ), INTERVAL 1 DAY ))
and 1 != 1
and epjv.level_code like concat("%", trim(#{$levelType$}), "%")
order by EPJV.POS_ID
